module.exports = (function() {
    var dgram = require("dgram"),
    udpSocket = null,
    socketio = require("socket.io"),
    oscParser = require("./OscParser"),

    io = socketio,
    init = function(params) {
        udpSocket = dgram.createSocket("udp4");
        udpSocket.on("listening", onSocketListening);
        udpSocket.bind(params.oscPort, params.oscHost);

        this.io = this.io.listen(params.socketPort);
        this.io.enable("browser client minification");
        this.io.enable("browser client etag");
        this.io.enable("browser client gzip");
        this.io.set("log level", 1);
        this.io.set("transports", [
            "websocket",
            "flashsocket",
            "htmlfile",
            "xhr-polling",
            "jsonp-polling"
        ]);
        this.io.sockets.on("connection", onSocketConnection);
    },

    onSocketListening = function() {
        var address = udpSocket.address();
        console.log("TuioServer listening on: " + address.address + ":" + address.port);
    },

    onSocketConnection = function(socket) {
        udpSocket.on("message", function(msg) {
            socket.emit("osc", oscParser.decode(msg));
        });
    };

    return {
        init: init,
        io: io
    };
}());
